import React from 'react'

import PlaySwitch from '../controls/PlaySwitch'
import ToggleSwitch from '../controls/ToggleSwitch'
import Slider from '../controls/Slider'
import Knob from '../controls/Knob'
import ButtonSet from '../controls/ButtonSet'

export default class Chorus extends React.Component {
  constructor(props) {
    super(props)
  }

  render() {
    const set = ['sine', 'square', 'triangle', 'sawtooth']

    const {
      name,
      effect,
      wet,
      on,
      toggleEffect,
      changeEffectWetValue,
      changeEffectValue
    } = this.props

    return (
      <div className="Effect chorus">
        <ToggleSwitch value={''} current={on} handleClick={toggleEffect} />
        <Slider
          name={name}
          property="wet"
          min="0"
          max="1"
          value={wet}
          handleValueChange={changeEffectWetValue}
        />
        <Slider
          name={name}
          property="frequency.value"
          min="0"
          max="100"
          value={effect.frequency.value}
          handleValueChange={changeEffectValue}
        />
        <Slider
          name={name}
          property="delayTime"
          min="0"
          max="10"
          on={on}
          value={effect.delayTime}
          handleValueChange={changeEffectValue}
        />
        <Slider
          name={name}
          property="depth"
          min="0"
          max="1"
          on={on}
          value={effect.depth}
          handleValueChange={changeEffectValue}
        />
        <ButtonSet
          name={name}
          property="type"
          set={set}
          value={effect.type}
          handleValueChange={changeEffectValue}
        />
        <Slider
          name={name}
          property="spread"
          min="0"
          max="360"
          on={on}
          value={effect.spread}
          handleValueChange={changeEffectValue}
        />
      </div>
    )
  }
}
