import React from 'react'
import Menubar from '../components/Menubar'

export default class Hello extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      color: 'red'
    }

    this.handleClick = this.handleClick.bind(this)
  }

  handleClick(elementName) {
    if (elementName == 'avatar') {
      const avatarColor = this.state.color == 'red' ? 'blue' : 'red'
    } else if (elementName == 'button') {
      const buttonColor = this.state.color == 'red' ? 'blue' : 'red'
    }

    this.setState({
      avatar: {
        color: avatarColor ? avatarColor : this.state.avatar.color
      },
      button: {
        color: buttonColor ? buttonColor : this.state.button.color
      }
    })
  }

  render() {
    const { name } = this.props
    const { color } = this.state

    return (
      <div>
        Hello {name}!
        <Menubar name={name} color={color} handleClick={this.handleClick} />
      </div>
    )
  }
}
