import ToggleSwitch from '../components/ToggleSwitch'
import Slider from '../components/Slider'
import Knob from '../components/Knob'

export default class Synth extends React.Component {
  constructor(props) {
    super(props)

    let distortion = new Tone.Distortion({
      distortion: 0,
      oversample: 'none'
    })

    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: 'sine',
      depth: 1,
      baseFrequency: 200,
      octaves: 2.6,
      filter: {
        type: 'lowpass',
        rolloff: -12,
        Q: 1
      }
    })

    let feedbackDelay1 = new Tone.FeedbackDelay({
      delayTime: '32n',
      maxDelay: 0.8
    })

    let feedbackDelay2 = new Tone.FeedbackDelay({
      delayTime: '8n',
      maxDelay: 0.8
    })

    let tremolo = new Tone.Tremolo({
      frequency: 10,
      type: 'sine',
      depth: 2,
      spread: 180
    })

    let autoPanner = new Tone.AutoPanner({
      frequency: 10,
      type: 'sine',
      depth: 100
    })

    let autoWah = new Tone.AutoWah({
      baseFrequency: 0,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    })

    let bitCrusher = new Tone.BitCrusher({
      bits: 4
    })

    let chebyshev = new Tone.Chebyshev({
      order: 50,
      oversample: 'none'
    })

    let chorus = new Tone.Chorus({
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      type: 'sine',
      spread: 180
    })

    let convolver = new Tone.Convolver({
      onload: Tone.noOp,
      normalize: true
    })

    let effect = new Tone.Effect({
      wet: 1
    })

    let feedbackEffect = new Tone.FeedbackEffect({
      feedback: 0.125
    })

    let freeverb = new Tone.Freeverb({
      roomSize: 0.7,
      dampening: 3000
    })

    let jcReverb = new Tone.JCReverb({
      roomSize: 0.5
    })

    let phaser = new Tone.Phaser({
      frequency: 0.5,
      octaves: 3,
      stages: 10,
      Q: 10,
      baseFrequency: 350
    })

    let pingPongDelay = new Tone.PingPongDelay({
      delayTime: 0.25,
      maxDelayTime: 1
    })

    let pitchShift = new Tone.PitchShift({
      pitch: 0,
      windowSize: 0.1,
      delayTime: 0,
      feedback: 0
    })

    let reverb = new Tone.Reverb({
      decay: 1.5,
      preDelay: 0.01
    })

    let stereoWidener = new Tone.StereoWidener({
      width: 0.5
    })

    let vibrato = new Tone.Vibrato({
      maxDelay: 0.005,
      frequency: 5,
      depth: 0.1,
      type: 'sine'
    })

    distortion.wet.value = 0
    autoFilter.wet.value = 0
    feedbackDelay1.wet.value = 0
    feedbackDelay2.wet.value = 0
    tremolo.wet.value = 0
    autoPanner.wet.value = 0
    autoWah.wet.value = 0
    bitCrusher.wet.value = 0
    chebyshev.wet.value = 0
    chorus.wet.value = 0
    convolver.wet.value = 0
    effect.wet.value = 0
    feedbackEffect.wet.value = 0
    freeverb.wet.value = 0
    jcReverb.wet.value = 0
    phaser.wet.value = 0
    pingPongDelay.wet.value = 0
    pitchShift.wet.value = 0
    reverb.wet.value = 0
    stereoWidener.wet.value = 0
    vibrato.wet.value = 0

    let synth = new Tone.PolySynth() //.toMaster()

    synth.chain(
      distortion,
      autoFilter,
      feedbackDelay1,
      feedbackDelay2,
      tremolo,
      autoPanner,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      effect,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      vibrato,
      Tone.Master
    )

    let loop1 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('C2', '8n', time)
    }, '4n')

    let loop2 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('E2', '32n', time)
    }, '32n')

    let loop3 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('D2', '1n', time)
    }, '1n')

    let loop4 = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('E2', '32n', time)
    }, '32n')

    let loopBeat = new Tone.Loop(beat, '8n')
    console.log(baseBeat)

    let baseBeat = new Tone.MembraneSynth({
      pitchDecay: 0.01,
      octaves: 2,
      oscillator: {
        type: 'sine'
      },
      envelope: {
        attack: 0.1,
        decay: 0.4,
        sustain: 1,
        release: 1.4,
        attackCurve: 'sine'
      }
    })

    function beat(time) {
      baseBeat.triggerAttackRelease('8n', time)
    }

    let noise = new Tone.NoiseSynth({
      noise: {
        type: 'brown'
      },
      envelope: {
        attack: 1.5,
        decay: 0.01,
        sustain: 1
      }
    })

    this.state = {
      lastChange: Date.now(),
      randomDelay: 100,
      autoFilter: {
        effect: autoFilter,
        wet: 0,
        on: false
      },
      feedbackDelay1: {
        effect: feedbackDelay1,
        wet: 0,
        on: false
      },
      feedbackDelay2: {
        effect: feedbackDelay2,
        wet: 0,
        on: false
      },
      tremolo: {
        effect: tremolo,
        wet: 0,
        on: false
      },
      distortion: {
        effect: distortion,
        wet: 0,
        on: false,
        oversample: ['none', '2x', '4x']
      },
      autoPanner: {
        effect: autoPanner,
        wet: 0,
        on: false
      },
      autoWah: {
        effect: autoWah,
        wet: 0,
        on: false
      },
      bitCrusher: {
        effect: bitCrusher,
        wet: 0,
        on: false
      },
      chebyshev: {
        effect: chebyshev,
        wet: 0,
        on: false
      },
      chorus: {
        effect: chorus,
        wet: 0,
        on: false
      },
      convolver: {
        effect: convolver,
        wet: 0,
        on: false
      },
      effect: {
        effect: effect,
        wet: 0,
        on: false
      },
      feedbackEffect: {
        effect: feedbackEffect,
        wet: 0,
        on: false
      },
      freeverb: {
        effect: freeverb,
        wet: 0,
        on: false
      },
      jcReverb: {
        effect: jcReverb,
        wet: 0,
        on: false
      },
      phaser: {
        effect: phaser,
        wet: 0,
        on: false
      },
      pingPongDelay: {
        effect: pingPongDelay,
        wet: 0,
        on: false
      },
      pitchShift: {
        effect: pitchShift,
        wet: 0,
        on: false
      },
      reverb: {
        effect: reverb,
        wet: 0,
        on: false
      },
      stereoWidener: {
        effect: stereoWidener,
        wet: 0,
        on: false
      },
      vibrato: {
        effect: vibrato,
        wet: 0,
        on: false
      },
      synth: {
        instrument: synth,
        on: false
      },
      loop1: {
        loop: loop1,
        on: false
      },
      loop2: {
        loop: loop2,
        on: false
      },
      loop3: {
        loop: loop3,
        on: false
      },
      loop4: {
        loop: loop4,
        on: false
      },
      noise: {
        sound: noise,
        on: false
      },
      loopBeat: {
        loop: loopBeat,
        on: false
      },
      baseBeat: {
        loop: loopBeat,
        on: false
      },
      beat: {
        loop: loopBeat,
        on: false
      }
    }

    _.bindAll(
      this,
      'toggleLoop',
      'toggleEffect',
      'changeEffectWetValue',
      'changeDistortionValue',
      'changeDistortionOversampleValue',
      'changeAutoWahSensitivity',
      'changeAutoWahBaseFrequency',
      'generateRandom',
      'getRandomArbitrary',
      'componentDidMount',
      'toggleNoiseSynth'
    )

    Tone.Transport.bpm.value = 23 //115
    Tone.Transport.start()
    loopBeat.start()
  }

  componentDidMount() {
    // this.generateRandom()
  }

  getRandomArbitrary(min, max) {
    return Math.floor(Math.random() * (max - min)) + min
  }

  generateRandom() {
    const { lastChange, randomDelay } = this.state
    if (Date.now() - lastChange >= randomDelay) {
      this.setState({
        lastChange: Date.now,
        randomDelay: this.getRandomArbitrary(100, 3000)
      })
    }
    // this.generateRandom()
  }

  toggleLoop(loopName) {
    let { loop, on } = this.state[loopName]

    on == true ? loop.stop() : loop.start('0m')

    this.setState({
      [`${loopName}`]: {
        loop: loop,
        on: !on
      }
    })
  }

  toggleNoiseSynth(noise) {
    let { sound, on } = this.state.noise

    on == true ? sound.stop() : sound.start('0m')

    this.setState({
      noise: {
        sound: noise,
        on: !on
      }
    })
  }

  toggleEffect(effectName) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? 0 : wet
    on = !on

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }

  changeEffectWetValue(effectName, value) {
    let { effect, wet, on } = this.state[effectName]

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      [`${effectName}`]: {
        effect,
        wet,
        on
      }
    })
  }
  changeDistortionValue(effectName, value) {
    let { effect, wet, on } = this.state.distortion

    effect.distortion = value

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    })
  }

  changeDistortionOversampleValue(value) {
    let { effect, wet, on } = this.state.distortion

    effect.wet.value = 'none' == true ? 0 : wet
    on = !on

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoWahSensitivity(value) {
    let { effect, wet, on } = this.state.autoWah
    console.log(value)
    effect.sensitivity = value

    this.setState({
      autoWah: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoWahBaseFrequency(name, value) {
    let { effect, wet, on } = this.state.autoWah
    effect.baseFrequency = value

    this.setState({
      autoWah: {
        effect,
        wet,
        on
      }
    })
  }

  render() {
    let {
      distortion,
      autoWah,
      synth,
      loop1,
      loop2,
      loop3,
      loop4,
      noise,
      loopBeat,
      baseBeat
    } = this.state
    let { toggleEffect } = this
    return (
      <div className="container">
        <div className="synthsPlay">
          <div className="playButton">
            <h3>Loop1</h3>
            <PlaySwitch
              name="play"
              value={loop1.on}
              handleToggleClick={() => {
                this.toggleLoop('loop1')
              }}
            />
          </div>
          <div className="playButton">
            <h3>Noise</h3>
            <PlaySwitch
              name="play"
              value={noise.on}
              handleToggleClick={this.toggleNoiseSynth}
            />
          </div>
          <div className="playButton">
            <h3> Pattern</h3>
            <PlaySwitch
              name="play"
              value={loop3.on}
              handleToggleClick={() => {
                this.toggleLoop('loop3')
              }}
            />
          </div>
          <div className="playButton">
            <h3>Loop4</h3>
            <PlaySwitch
              name="play"
              value={loop4.on}
              handleToggleClick={() => {
                this.toggleLoop('loop4')
              }}
            />
          </div>
          <div className="playButton">
            <h3>LoopBeat</h3>
            <PlaySwitch
              name="play"
              value={loopBeat.on}
              handleToggleClick={() => {
                this.toggleLoop('loopBeat')
              }}
            />
          </div>
        </div>
        <div className="innerContainer">
          <h2>Toggle Distortion</h2>
          <ToggleSwitch
            value="distortion"
            current={distortion.on}
            handleClick={() => {
              toggleEffect('distortion')
            }}
          />
          <h3>Change Distortion Oversample Value</h3>
          <ToggleSwitch
            value="none"
            current={distortion.on}
            handleClick={this.changeDistortionOversampleValue}
          />
          <ToggleSwitch
            value="2x"
            current={distortion.on}
            handleClick={this.changeDistortionOversampleValue}
          />
          <ToggleSwitch
            value="4x"
            current={distortion.on}
            handleClick={this.changeDistortionOversampleValue}
          />
          <h3>Change Distortion Wet Value</h3>
          <Slider
            name="distortion"
            min="0"
            max="1"
            value={distortion.effect.wet.value}
            handleValueChange={this.changeEffectWetValue}
          />
          <h3>Change Distortion Value</h3>
          <Slider
            name="distortion"
            min="0"
            max="100"
            value={distortion.effect.distortion}
            handleValueChange={this.changeDistortionValue}
          />
        </div>
        <div className="innerContainer">
          <h2>Toggle AutoWah</h2>
          <ToggleSwitch
            value="autoWah"
            current={autoWah.on}
            handleClick={() => {
              toggleEffect('autoWah')
            }}
          />
          <h3>Change AutoWah Wet Value</h3>
          <Slider
            name="autoWah"
            min="0"
            max="1"
            value={autoWah.effect.wet.value}
            handleValueChange={this.changeEffectWetValue}
          />
          <h3>Toggle AutoWah Frequency</h3>
          <Slider
            name="autoWah"
            min="0"
            max="1000"
            value={autoWah.effect.baseFrequency}
            handleValueChange={this.changeAutoWahBaseFrequency}
          />
          <h3>Toggle AutoWah Octaves Sensitivity</h3>
          <Knob
            min="-10"
            max="10"
            value={autoWah.effect.sensitivity}
            handleValueChange={this.changeAutoWahSensitivity}
          />
          Toggle AutoWah Gain Toggle AutoWah Attack Toggle AutoWah Release
        </div>
      </div>
    )
  }
}
