import Tone from 'tone'

// function basePart() {
//   return new Tone.Part({
//     callback: 'Tone.noOp',
//     loop: false,
//     loopEnd: '1m',
//     loopStart: '0',
//     playbackRate: '1',
//     probability: '1',
//     humanize: false,
//     mute: false,
//     events: []
//   })
// }

function basePart(synth) {
  return new Tone.Part(
    {
      callback: 'Tone.noOp',
      loop: false,
      loopEnd: '1m',
      loopStart: '0',
      playbackRate: '1',
      probability: '1',
      humanize: false,
      mute: false,
      events: []
    },
    function(time, note) {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    },
    [
      {
        time: '4:1:0',
        note: 'E#2',
        velocity: 1
      },
      {
        time: '5:1:0',
        note: 'B2',
        velocity: 1
      },
      {
        time: '5:3:0',
        note: 'E#3',
        velocity: 1
      },
      {
        time: '10:1:0',
        note: 'B#3',
        velocity: 1
      },
      {
        time: '11:1:0',
        note: 'E#2',
        velocity: 0.5
      },
      {
        time: '17:1:0',
        note: 'B#2',
        velocity: 1
      },
      {
        time: '18:1:0',
        note: 'B2',
        velocity: 1
      },
      {
        time: '18:3:0',
        note: 'E#4',
        velocity: 1
      },
      {
        time: '24:3:0',
        note: 'B#4',
        velocity: 1
      }
    ]
  )
}
export { basePart }
