import _ from 'lodash'
import React from 'react'
import Tone from 'tone'

import PlaySwitch from '../controls/PlaySwitch'
import ToggleSwitch from '../controls/ToggleSwitch'
import Slider from '../controls/Slider'
import Knob from '../controls/Knob'
import ButtonSet from '../controls/ButtonSet'

export default class NotesSynth extends React.Component {
  constructor(props) {
    super(props)
    _.bindAll(this, 'handleValueChange')
  }

  handleValueChange(name, property, value) {
    const { changeSynthValue } = this.props
    changeSynthValue(name, property, value)
  }

  render() {
    const { synth, instrument, on, togglePlay } = this.props
    const {
      type,
      count,
      spread,
      frequency,
      detune,
      partialCount
    } = synth.instrument

    const set = ['sine', 'square', 'triangle', 'sawtooth', 'fatsawtooth']

    return (
      <div className="innerContainer">
        <div className="playButton">
          <ToggleSwitch value="play" current={on} handleClick={togglePlay} />
        </div>
        <ButtonSet
          name={synth}
          property="type"
          set={set}
          value={type}
          handleValueChange={this.handleValueChange}
        />
        <Slider
          name={synth}
          property="count"
          min="0"
          max="100"
          value={count}
          handleValueChange={this.handleValueChange}
        />
        <Slider
          name={synth}
          property="spread"
          min="0"
          max="100"
          value={spread}
          handleValueChange={this.handleValueChange}
        />
        <Slider
          name={synth}
          property="frequency"
          min="0"
          max="5000"
          value={frequency}
          handleValueChange={this.handleValueChange}
        />
        <Slider
          name={synth}
          property="detune"
          min="0"
          max="10"
          value={detune}
          handleValueChange={this.handleValueChange}
        />
        <Slider
          name={synth}
          property="partialCount"
          min="0"
          max="100"
          value={partialCount}
          handleValueChange={this.handleValueChange}
        />
      </div>
    )
  }
}
