      metalDistortion,
      metalFreeverb,
      noiseSynth,
      loop1,
      loop2,
      loop3,
      loop4,
      loop5,
      volume
    } = this.state

    let {
      toggleEffect,
      toggleLoop,
      changeSynthValue,
      changeVolumeValue,
      changeEffectWetValue,
      changeEffectValue,
      changeEffectFilterValue
    } = this

    return (
      <div className="container">
        <div className="common">
