import React from 'react'
import Menubar from '../components/Menubar'

export default class Thereminvox extends React.Component {
  constructor(props) {
    super(props)

    const audioContext = new (window.AudioContext ||
      window.webkitAudioContext)()
    let oscillator = audioContext.createOscillator()
    oscillator.type = 'square'

    this.state = {
      context: audioContext,
      oscillator: oscillator,
      playing: false
    }

    this.handleMouseMove = this.handleMouseMove.bind(this)
    this.handleStart = this.handleStart.bind(this)
    this.handleStop = this.handleStop.bind(this)
  }

  componentDidMount() {
    document.addEventListener('mousemove', this.handleMouseMove)
  }

  handleMouseMove(e) {
    console.log(e.clientX, e.clientY)
  }
  handleStart() {
    let { audioContext } = this.state
    // let { oscillator } = this.oscillator
    let oscillator = audioContext.createOscillator()
    oscillator.type = 'square'
    oscillator.frequency.setValueAtTime(33, audioContext.currentTime)
    oscillator.connect(audioContext.destination)
    oscillator.start()

    this.setState({
      oscillator: oscillator,
      playing: true
    })
  }

  handleStop() {
    let { audioContext } = this.state
    oscillator.stop()

    this.setState({
      oscillator: oscillator,
      playing: false
    })
  }

  changeFrequency() {}

  render() {
    return (
      <div>
        <div onClick={this.handleStart}>Start</div>
        <div onClick={this.handleStop}>Stop</div>
      </div>
    )
  }
}
