import Tone from 'tone'

function part1(synth) {
  return new Tone.Part(
    function(time, note) {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    },
    [
      {
        time: '4:1:0',
        noteName: 'E#2',
        velocity: 1,
        duration: '8n'
      },
      {
        time: '5:1:0',
        noteName: 'B2',
        velocity: 1,
        duration: '8n'
      },
      {
        time: '5:3:0',
        noteName: 'E#3',
        velocity: 1,
        duration: '8n'
      },
      {
        time: '10:1:0',
        noteName: 'B#3',
        velocity: 1,
        duration: '8n'
      },
      {
        time: '11:1:0',
        noteName: 'E#2',
        velocity: 0.5,
        duration: '4n'
      },
      {
        time: '17:1:0',
        noteName: 'B#2',
        velocity: 1,
        duration: '4n'
      },
      {
        time: '18:1:0',
        noteName: 'B2',
        velocity: 1,
        duration: '4n'
      },
      {
        time: '18:3:0',
        noteName: 'E#4',
        velocity: 1,
        duration: '4n'
      },
      {
        time: '24:3:0',
        noteName: 'B#4',
        velocity: 1,
        duration: '4n'
      }
    ]
  )
}
export { part1 }
