import _ from 'lodash'
import React from 'react'
import Tone from 'tone'

import PlaySwitch from '../components/PlaySwitch'
import ToggleSwitch from '../components/ToggleSwitch'
import ToggleSwitchWithValue from '../components/ToggleSwitchWithValue'
import Slider from '../components/Slider'
import Knob from '../components/Knob'

export default class Synth extends React.Component {
  constructor(props) {
    super(props)

    let distortion = new Tone.Distortion({
      distortion: 0,
      oversample: 'none'
    })

    let autoFilter = new Tone.AutoFilter({
      frequency: 1,
      type: 'sine',
      depth: 1,
      baseFrequency: 200,
      octaves: 2.6,
      filter: {
        type: 'lowpass',
        rolloff: -12,
        Q: 1
      }
    })

    let feedbackDelay1 = new Tone.FeedbackDelay({
      delayTime: '32n',
      maxDelay: 0.8
    })

    let feedbackDelay2 = new Tone.FeedbackDelay({
      delayTime: '8n',
      maxDelay: 0.8
    })

    let tremolo = new Tone.Tremolo({
      frequency: 10,
      type: 'sine',
      depth: 2,
      spread: 180
    })

    let autoPanner = new Tone.AutoPanner({
      frequency: 10,
      type: 'sine',
      depth: 100
    })

    let autoWah = new Tone.AutoWah({
      baseFrequency: 100,
      octaves: 6,
      sensitivity: 0,
      Q: 2,
      gain: 2,
      follower: {
        attack: 0.3,
        release: 0.5
      }
    })

    let bitCrusher = new Tone.BitCrusher({
      bits: 4
    })

    let chebyshev = new Tone.Chebyshev({
      order: 50,
      oversample: 'none'
    })

    let chorus = new Tone.Chorus({
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      type: 'sine',
      spread: 180
    })

    let convolver = new Tone.Convolver({
      onload: Tone.noOp,
      normalize: true
    })

    let effect = new Tone.Effect({
      wet: 1
    })

    let feedbackEffect = new Tone.FeedbackEffect({
      feedback: 0.125
    })

    let freeverb = new Tone.Freeverb({
      roomSize: 0.7,
      dampening: 3000
    })

    let jcReverb = new Tone.JCReverb({
      roomSize: 0.5
    })

    let phaser = new Tone.Phaser({
      frequency: 0.5,
      octaves: 3,
      stages: 10,
      Q: 10,
      baseFrequency: 350
    })

    let pingPongDelay = new Tone.PingPongDelay({
      delayTime: 0.25,
      maxDelayTime: 1
    })

    let pitchShift = new Tone.PitchShift({
      pitch: 0,
      windowSize: 0.1,
      delayTime: 0,
      feedback: 0
    })

    let reverb = new Tone.Reverb({
      decay: 1.5,
      preDelay: 0.01
    })

    let stereoWidener = new Tone.StereoWidener({
      width: 0.5
    })

    let vibrato = new Tone.Vibrato({
      maxDelay: 0.005,
      frequency: 5,
      depth: 0.1,
      type: 'sine'
    })

    distortion.wet.value = 0
    autoFilter.wet.value = 0
    feedbackDelay1.wet.value = 0
    feedbackDelay2.wet.value = 0
    tremolo.wet.value = 0
    autoPanner.wet.value = 0
    autoWah.wet.value = 0
    bitCrusher.wet.value = 0
    chebyshev.wet.value = 0
    chorus.wet.value = 0
    convolver.wet.value = 0
    effect.wet.value = 0
    feedbackEffect.wet.value = 0
    freeverb.wet.value = 0
    jcReverb.wet.value = 0
    phaser.wet.value = 0
    pingPongDelay.wet.value = 0
    pitchShift.wet.value = 0
    reverb.wet.value = 0
    stereoWidener.wet.value = 0
    vibrato.wet.value = 0

    let synth = new Tone.Synth() //.toMaster()
    synth.chain(
      distortion,
      autoFilter,
      feedbackDelay1,
      feedbackDelay2,
      tremolo,
      autoPanner,
      autoWah,
      bitCrusher,
      chebyshev,
      chorus,
      convolver,
      effect,
      feedbackEffect,
      freeverb,
      jcReverb,
      phaser,
      pingPongDelay,
      pitchShift,
      reverb,
      stereoWidener,
      vibrato,
      Tone.Master
    )

    let loop = new Tone.Loop(function(time) {
      synth.triggerAttackRelease('C2', '8n', time)
    }, '4n')

    this.state = {
      autoFilter: {
        effect: autoFilter,
        wet: 0,
        on: false
      },
      feedbackDelay1: {
        effect: feedbackDelay1,
        wet: 0,
        on: false
      },
      feedbackDelay2: {
        effect: feedbackDelay2,
        wet: 0,
        on: false
      },
      tremolo: {
        effect: tremolo,
        wet: 0,
        on: false
      },
      distortion: {
        effect: distortion,
        wet: 0,
        on: false,
        oversample: ['none', '2x', '4x']
      },
      autoPanner: {
        effect: autoPanner,
        wet: 0,
        on: false
      },
      autoWah: {
        effect: autoWah,
        wet: 0,
        on: false
      },
      bitCrusher: {
        effect: bitCrusher,
        wet: 0,
        on: false
      },
      chebyshev: {
        effect: chebyshev,
        wet: 0,
        on: false
      },
      chorus: {
        effect: chorus,
        wet: 0,
        on: false
      },
      convolver: {
        effect: convolver,
        wet: 0,
        on: false
      },
      effect: {
        effect: effect,
        wet: 0,
        on: false
      },
      feedbackEffect: {
        effect: feedbackEffect,
        wet: 0,
        on: false
      },
      freeverb: {
        effect: freeverb,
        wet: 0,
        on: false
      },
      jcReverb: {
        effect: jcReverb,
        wet: 0,
        on: false
      },
      phaser: {
        effect: phaser,
        wet: 0,
        on: false
      },
      pingPongDelay: {
        effect: pingPongDelay,
        wet: 0,
        on: false
      },
      pitchShift: {
        effect: pitchShift,
        wet: 0,
        on: false
      },
      reverb: {
        effect: reverb,
        wet: 0,
        on: false
      },
      stereoWidener: {
        effect: stereoWidener,
        wet: 0,
        on: false
      },
      vibrato: {
        effect: vibrato,
        wet: 0,
        on: false
      },
      synth: {
        instrument: synth,
        on: false
      },
      loop
    }

    _.bindAll(
      this,
      'toggleSynth',
      'toggleDistortion',
      'changeDistortionWetValue',
      'changeDistortionValue',
      'changeDistortionOversampleValue'
    )
  }

  toggleSynth() {
    let { synth, loop } = this.state
    let { instrument, on } = synth

    on == true ? loop.stop() : loop.start('0m')

    this.setState({
      synth: {
        instrument: instrument,
        on: !on
      }
    })

    Tone.Transport.bpm.value = 115
    Tone.Transport.start()
  }

  toggleDistortion() {
    let { effect, wet, on } = this.state.distortion

    effect.wet.value = on == true ? 0 : wet
    on = !on

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    })
  }

  changeDistortionWetValue(value) {
    let { effect, wet, on } = this.state.distortion

    effect.wet.value = on == true ? value : 0
    wet = value

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    })
  }
  changeDistortionValue(value) {
    let { effect, wet, on } = this.state.distortion

    effect.distortion = value

    this.setState({
      distortion: {
        effect,
        wet,
        on
      }
    })
  }

  changeDistortionOversampleValue(value) {
    let { effect, wet, on } = this.state.distortion

    oversample.distortion = value

    this.setState({
      distortion: {
        effect,
        wet,
        on,
        oversample
      }
    })
  }

  toggleAutoWah() {
    let { effect, wet, on } = this.state.AutoWah

    effect.wet.value = on == true ? 0 : wet
    on = !on

    this.setState({
      AutoWah: {
        effect,
        wet,
        on
      }
    })
  }

  changeAutoWahSensitivity() {
    let { effect, wet, on } = this.state.autoWah

    effect.autoWah = value

    this.setState({
      autoWah: {
        effect,
        wet,
        on
      }
    })
  }

  render() {
    let { distortion, synth } = this.state
    return (
      <div>
        Play Synth
        <PlaySwitch
          name="play"
          value={synth.on}
          handleToggleClick={this.toggleSynth}
        />
        Toggle Distortion
        <ToggleSwitch
          value="distortion"
          current={distortion.on}
          handleClick={this.toggleDistortion}
        />
        Change Distortion Oversample Value
        <ToggleSwitchWithValue
          value="none"
          current={distortion.on}
          handleClick={this.toggleDistortionOversampleValue}
        />
        <ToggleSwitchWithValue
          value="2x"
          current={distortion.on}
          handleClick={this.toggleDistortionOversampleValue}
        />
        <ToggleSwitchWithValue
          value="4x"
          current={distortion.oversample}
          handleClick={this.toggleDistortionOversampleValue}
        />
        Change Distortion Wet Value
        <Slider
          min="0"
          max="1"
          value={distortion.effect.wet.value}
          handleValueChange={this.changeDistortionWetValue}
        />
        Change Distortion Value
        <Slider
          min="0"
          max="100"
          value={distortion.effect.distortion}
          handleValueChange={this.changeDistortionValue}
        />
        Toggle AutoWah
        <ToggleSwitch
          value="autoWah"
          current={autoWah.on}
          handleClick={this.toggleAutoWah}
        />
        Toggle AutoWah Frequency Toggle AutoWah Octaves Toggle AutoWah
        Sensitivity
        <Knob
          min="-10"
          max="10"
          value={distortion.effect.autoWah}
          handleValueChange={this.changeAutoWahSensitivity}
        />
        Toggle AutoWah Gain Toggle AutoWah Attack Toggle AutoWah Release
      </div>
    )
  }
}
